import React, {useContext} from 'react';

import Context from "../Context"

const WeatherData = (props) => {

const  {weather} = useContext(Context)
const {hourly} = weather

    return (
        
        <div className="weather-data">
  <p className="weather__tagline">Weather data for <span className="weather-data__city">Slovenske Konjice</span></p>
  <div className="weather-data__box">
    <span className="weather-data__property">
      <p className="weather-data__title">Hourly values</p>
      <p className="weather-data__value">{props.weather.hourly}</p>
    </span>
  </div>
</div>
    )
}

export default WeatherData;
